all_by_customer_and_timestamp.map:
function(doc) {
  if (doc.doctype=='address' && doc.timestamp && doc.customer) {
               emit(doc.customer + "-" + doc.timestamp, doc.processed);
  }
}

unprocessed_by_timestamp.map:
function(doc) {
  if (doc.doctype=='address' && doc.timestamp && (!doc.processed || doc.processed=='unprocessed')) {
               emit(doc.timestamp, null);
  }
}

paid_by_timestamp.map:
function(doc) {
  if (doc.doctype=='address' && doc.timestamp && (doc.processed=='paid')) {
               emit(doc.timestamp,  new Date( doc.timestamp * 1000).toISOString().replace('T', ' ') );
  }
}

paid_and_sweeped_by_timestamp.map:
function(doc) {
  if (doc.doctype=='address' && doc.timestamp && (doc.processed=='paid_and_sweeped')) {
               emit(doc.timestamp, new Date( doc.timestamp * 1000).toISOString().replace('T', ' '));
  }
}
